// src/FitnessSuggestions.js
import React, { useState } from 'react';
import './FitnessSuggestions.css'; // Assuming you have a CSS file for styling

const FitnessSuggestions = () => {
  const [goal, setGoal] = useState('');
  const [suggestions, setSuggestions] = useState('');

  const handleGoalChange = (event) => {
    setGoal(event.target.value);
    generateSuggestions(event.target.value);
  };

  const generateSuggestions = (selectedGoal) => {
    let suggestionText = '';
    switch (selectedGoal) {
      case 'weight-loss':
        suggestionText = 'To lose weight, focus on cardio exercises like running, cycling, and swimming. Try to work out at least 4-5 times a week for 30-60 minutes.';
        break;
      case 'muscle-gain':
        suggestionText = 'For muscle gain, incorporate strength training exercises such as weight lifting and resistance workouts. Aim for 3-4 sessions a week with a focus on progressive overload.';
        break;
      case 'general-fitness':
        suggestionText = 'To maintain general fitness, mix both cardio and strength training exercises. Aim for at least 150 minutes of moderate aerobic activity each week, along with two days of strength training.';
        break;
      default:
        suggestionText = '';
    }
    setSuggestions(suggestionText);
  };

  return (
    <div className="fitness-suggestions">
      <h1>Fitness Suggestions</h1>
      <div className="input-group">
        <label>Select Your Fitness Goal:</label>
        <select value={goal} onChange={handleGoalChange}>
          <option value="">Select</option>
          <option value="weight-loss">Weight Loss</option>
          <option value="muscle-gain">Muscle Gain</option>
          <option value="general-fitness">General Fitness</option>
        </select>
      </div>
      {suggestions && (
        <div className="suggestion-box">
          <h2>Suggestions:</h2>
          <p>{suggestions}</p>
        </div>
      )}
    </div>
  );
};

export default FitnessSuggestions;
