// src/BMICalculator.js
import React, { useState } from 'react';
import './BMICalculator.css'; // Assuming you have a CSS file for styling

const BMICalculator = () => {
  const [height, setHeight] = useState('');
  const [weight, setWeight] = useState('');
  const [result, setResult] = useState('');

  const calculateBMI = () => {
    const heightValue = parseFloat(height);
    const weightValue = parseFloat(weight);

    // Check if inputs are valid numbers
    if (isNaN(heightValue) || isNaN(weightValue) || heightValue <= 100  || heightValue >= 250  || weightValue >= 150  || weightValue <= 0) {
      setResult("Please enter valid height and weight.");
      return;
    }

    const bmi = weightValue / ((heightValue / 100) ** 2);
    let category;

    if (bmi < 18.5) {
      category = "Underweight";
    } else if (bmi >= 18.5 && bmi < 25) {
      category = "Normal weight";
    } else if (bmi >= 25 && bmi < 30) {
      category = "Overweight";
    } else if (bmi >= 30 && bmi < 35) {
      category = "Obesity";
    } else {
      category = "Extra Obesity";
    }


   // Display result
    setResult(`Your BMI is ${bmi.toFixed(1)} (${category}).`);
  };


  const deleteInput = () => {
    setHeight('');
    setWeight('');
    setResult('');
  };

  return (
    <div className="bmi-calculator">
      <h1>BMI Calculator</h1>
      <input
        type="number"
        placeholder="Height (cm)"
        value={height}
        onChange={(e) => setHeight(e.target.value)}
      />
      <input
        type="number"
        placeholder="Weight (kg)"
        value={weight}
        onChange={(e) => setWeight(e.target.value)}
      />
      <div className="button-container">
        <button onClick={calculateBMI}>Calculate</button>
        <button onClick={deleteInput}>Delete</button>
      </div>
      <div id="result">{result}</div>
    </div>
  );
}

export default BMICalculator;
